INSERT [dbo].[Article] ([ArticleID], [ArticleName], [Alias], [DescriptShort], [BlogID], [Images], [Author], [Content], [MetaTitle], [MetaKeyword], [MetaDescription], [News_hot], [CreatedAt], [Status]) VALUES (10, N'TUYỂN NHÂN VIÊN TRIỂN KHAI PHẦN MỀM', N'tuyen-nhan-vien-trien-khai-phan-mem', NULL, 1, N'/Upload/ImageDes/images/ha-img41_large.jpg', N'Admin', N'<p style="box-sizing: border-box; margin: 0px 0px 15px; font-family: NotoSerif, &quot;Helvetica Neue&quot;, Helvetica, Arial, sans-serif; font-size: 14px;"><img data-mce-src="//file.hstatic.net/1000248829/file/hg-ori-6_grande.jpg" data-mce-style="display: block; margin-left: auto; margin-right: auto;" src="https://file.hstatic.net/1000248829/file/hg-ori-6_grande.jpg" style="box-sizing: border-box; border: 0px none; max-width: 100%; vertical-align: middle; margin: 0px auto; display: block;" /></p>

<p align="center">C&ocirc;ng ty phần mế to&aacute;n doanh nghiệp Việt Nam đang cần tuyển:</p>

<p><strong>1.&nbsp;&nbsp;&nbsp;&nbsp;Vị tr&iacute; cần tuyển: &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;NH&Acirc;N VI&Ecirc;N TRIỂN KHAI PHẦN MỀM</strong></p>

<p><strong>2.&nbsp;&nbsp;&nbsp;&nbsp;Số lượng cần tuyển: &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 02 nh&acirc;n vi&ecirc;n</strong></p>

<p><strong>3.&nbsp;&nbsp;&nbsp;&nbsp;Nơi l&agrave;m việc:&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; H&agrave; Nội</strong></p>

<p><strong>4.&nbsp;&nbsp;&nbsp;&nbsp;M&ocirc; tả c&ocirc;ng việc:</strong></p>

<p style="margin-left:23.05pt;">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Sửa đổi, hiệu chỉnh phần mềm theo y&ecirc;u cầu kh&aacute;ch h&agrave;ng.</p>

<p style="margin-left:23.05pt;">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; C&agrave;i đặt, hướng dẫn, hỗ trợ kh&aacute;ch h&agrave;ng sử dụng phần mềm.</p>

<p style="margin-left:23.05pt;">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Trợ gi&uacute;p kh&aacute;ch h&agrave;ng c&aacute;c nghiệp vụ kế to&aacute;n&hellip;</p>

<p style="margin-left:23.05pt;">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Thực hiện c&aacute;c c&ocirc;ng việc theo đ&uacute;ng quy định của c&ocirc;ng ty.</p>

<p><strong>5.&nbsp;&nbsp;&nbsp;&nbsp;Y&ecirc;u cầu:</strong></p>

<p style="margin-left:23.05pt;">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Tốt nghiệp Đại học, Cao đẳng</p>

<p style="margin-left:23.05pt;">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Nắm vững kiến thức lập tr&igrave;nh cơ sở dữ liệu SQL Server, ưu ti&ecirc;n ứng vi&ecirc;n c&oacute; kiến thức về kế to&aacute;n hoặc c&oacute; kinh nghiệm c&ocirc;ng t&aacute;c trong lĩnh vực triển khai phần mềm</p>

<p style="margin-left:23.05pt;">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Khả năng giao tiếp tốt.</p>

<p style="margin-left:23.05pt;">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Chăm chỉ, cẩn thận, nhanh nhẹn.</p>

<p style="margin-left:23.05pt;">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Ưu ti&ecirc;n ứng vi&ecirc;n c&oacute; kinh nghiệm</p>

<p><strong>6. &nbsp; &nbsp;Quyền lợi v&agrave; nghĩa vụ:</strong></p>

<p style="margin-left:23.05pt;">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>C&aacute;c chế độ</strong>:</p>

<p style="margin-left:46.1pt;">o&nbsp;&nbsp;&nbsp; Mức thu nhập cơ bản&nbsp;<strong><em>(từ 7.000.000 đồng đến&nbsp;12.000.000 đồng / th&aacute;ng)</em></strong>. Trong đ&oacute;:</p>

<ul>
	<li style="margin-left: 69.15pt;">&nbsp;Mức lương hấp dẫn&nbsp;<em>(từ 7.000.000 đồng đến 12.000.000 đồng / th&aacute;ng)</em>.</li>
	<li style="margin-left: 69.15pt;">Thưởng theo dự &aacute;n thực tế.</li>
	<li style="margin-left: 69.15pt;">&nbsp;Phụ cấp ăn trưa&nbsp;<strong><em>~ 600.000 đồng / th&aacute;ng</em></strong></li>
</ul>

<p style="margin-left:46.1pt;">o&nbsp;&nbsp;&nbsp; ​Thu nhập mở rộng:</p>

<ul>
	<li style="margin-left: 69.15pt;">Thưởng doanh số theo gi&aacute; trị c&aacute;c hợp đồng triển khai.</li>
	<li style="margin-left: 69.15pt;">Phụ cấp tr&aacute;ch nhiệm&nbsp;</li>
</ul>

<p style="margin-left:46.1pt;">o&nbsp;&nbsp;&nbsp; C&ocirc;ng việc th&aacute;ch thức, m&ocirc;i trường l&agrave;m việc chuy&ecirc;n nghiệp.</p>

<p style="margin-left:46.1pt;">o&nbsp;&nbsp;&nbsp; Được k&yacute; hợp đồng lao động ch&iacute;nh thức v&agrave; hưởng c&aacute;c quyền lợi đ&uacute;ng luật lao động, hưởng c&aacute;c ch&iacute;nh s&aacute;ch đ&atilde;i ngộ xứng đ&aacute;ng, ph&ugrave; hợp với năng lực v&agrave; kinh nghiệm</p>

<p style="margin-left:46.1pt;">o&nbsp;&nbsp;&nbsp; Được đ&agrave;o tạo tại c&ocirc;ng ty hoặc tham gia c&aacute;c kh&oacute;a học b&ecirc;n ngo&agrave;i.</p>

<p style="margin-left:46.1pt;">o&nbsp;&nbsp;&nbsp; C&oacute; cơ hội mở rộng kiến thức về hệ thống quản trị doanh nghiệp (ERP) v&agrave; hệ thống kế to&aacute;n doanh nghiệp.</p>

<p style="margin-left:23.05pt;">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>Hồ sơ</strong>:</p>

<p style="margin-left:46.1pt;">o&nbsp;&nbsp;&nbsp; Sơ yếu l&yacute; lịch (c&oacute; x&aacute;c nhận của địa phương).</p>

<p style="margin-left:46.1pt;">o&nbsp;&nbsp;&nbsp; Đơn xin việc (viết tay).</p>

<p style="margin-left:46.1pt;">o&nbsp;&nbsp;&nbsp; Bằng tốt nghiệp đại học, cao đẳng (photo)</p>

<p style="margin-left:46.1pt;">o&nbsp;&nbsp;&nbsp; Giấy khai sinh, giấy kh&aacute;m sức khoẻ.</p>

<p style="margin-left:46.1pt;">o&nbsp;&nbsp;&nbsp; 02 ảnh 3x4, chứng minh nh&acirc;n d&acirc;n (photo).</p>

<p style="margin-left:46.1pt;">o&nbsp;&nbsp;&nbsp; C&aacute;c chứng chỉ văn bằng kh&aacute;c (nếu c&oacute;).</p>

<p style="margin-left:46.1pt;">o&nbsp;&nbsp;&nbsp; Bản t&oacute;m tắt qu&aacute; tr&igrave;nh học tập, kinh nghiệm l&agrave;m việc, kĩ năng chuy&ecirc;n m&ocirc;n... của c&aacute; nh&acirc;n (CV)</p>

<p style="margin-left:23.05pt;">&middot;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <strong>C&aacute;c bước phỏng vấn</strong>:</p>

<p style="margin-left:46.1pt;">o&nbsp;&nbsp;&nbsp; Nhận hồ sơ</p>

<p style="margin-left:46.1pt;">o&nbsp;&nbsp;&nbsp; Phỏng vấn trực tiếp.</p>

<p style="margin-left:46.1pt;">o&nbsp;&nbsp;&nbsp; Trả lời c&aacute;c c&acirc;u hỏi về kiến thức kế to&aacute;n v&agrave; lập tr&igrave;nh.</p>

<p><strong>7.&nbsp;&nbsp;&nbsp;&nbsp;Hạn&nbsp;nộp hồ sơ:&nbsp; 31/05/2018</strong></p>

<p><strong>8.&nbsp;&nbsp;&nbsp; Li&ecirc;n hệ: &nbsp; &nbsp;&nbsp;</strong></p>

