INSERT [dbo].[Product] ([ProductID], [ProductName], [Alias], [Images], [CategoryID], [DescriptShort], [Content], [Content2], [Content3], [MetaTitle], [MetaKeyword], [Author], [MetaDescription], [CreatedAt], [Status]) VALUES (1, N'Giải pháp quản lý tổng thể ERP Acpinet', N'giai-phap-quan-ly-tong-the-erp-acpinet', N'/Upload/ImageDes/files/AcpiNet.png', 4, N'AcpiNet là hệ thống phần mềm giúp cho doanh nghiệp quản lý tổng thể được tích hợp đủ các công cụ quản lý vế tài chính kế toán, kế hoạch, nhân sự tiền lương...', N'<p style="box-sizing: border-box; margin: 0px; padding: 0px; line-height: 25px; color: rgb(68, 68, 68); font-size: 14px; text-align: justify;"><img alt="" src="/Upload/ImageDes/images/bannerAcpinet(1).jpg" style="width: 848px; height: 434px;" /></p>

<ul style="box-sizing: border-box; padding-right: 0px; padding-left: 15px; line-height: 25px; margin: 0px; color: rgb(68, 68, 68); font-size: 14px; text-align: justify; list-style-type: circle;">
	<li style="box-sizing: border-box; padding: 0px; line-height: 25px; text-align: justify;">AcpiNet l&agrave; hệ thống phần mềm gi&uacute;p cho doanh nghiệp quản l&yacute; c&aacute;c hoạt động chủ chốt của m&igrave;nh theo một quy tr&igrave;nh thống nhất, từ: kế to&aacute;n, ph&acirc;n t&iacute;ch t&agrave;i ch&iacute;nh, quản l&yacute; mua h&agrave;ng, quản l&yacute; tồn kho, hoạch định v&agrave; quản l&yacute; sản xuất, quản l&yacute; hậu cần, quản l&yacute; quan hệ với kh&aacute;ch h&agrave;ng, v.v...</li>
	<li style="box-sizing: border-box; padding: 0px; line-height: 25px; text-align: justify;">Mục ti&ecirc;u tổng qu&aacute;t của hệ thống n&agrave;y l&agrave; đảm bảo c&aacute;c nguồn lực t&iacute;ch hợp của doanh nghiệp như nh&acirc;n lực, vật tư, m&aacute;y m&oacute;c v&agrave; tiền bạc c&oacute; sẵn với số lượng đủ khi cần, bằng c&aacute;ch sử dụng c&aacute;c c&ocirc;ng cụ hoạch định v&agrave; l&ecirc;n kế hoạch. N&oacute; cho ph&eacute;p doanh nghiệp cung cấp v&agrave; tổng hợp số liệu của nhiều hoạt động ri&ecirc;ng rẽ kh&aacute;c nhau để đạt được mục ti&ecirc;u tr&ecirc;n</li>
	<li style="box-sizing: border-box; padding: 0px; line-height: 25px; text-align: justify;">Hệ thống bao gồm nhiều ph&acirc;n hệ, từng ph&acirc;n hệ c&oacute; thể hoạt động độc lập nhưng ch&uacute;ng c&oacute; thể kết nối với nhau để tự động chia sẻ th&ocirc;ng tin với c&aacute;c ph&acirc;n hệ kh&aacute;c nhau nhằm tạo n&ecirc;n một hệ thống mạnh hơn</li>
	<li style="box-sizing: border-box; padding: 0px; line-height: 25px; text-align: justify;">AcpiNet được x&acirc;y dựng v&agrave; ph&aacute;t triển bằng c&aacute;c c&ocirc;ng cụ hiện đại nhất, lập tr&igrave;nh tr&ecirc;n ng&ocirc;n ngữ .NET, cơ sở dữ liệu SQL Server cho ph&eacute;p cập nhật v&agrave; khai th&aacute;c dữ liệu qua mạng Internet. Hỗ trợ Unicode, cho ph&eacute;p nhập nhiều ng&ocirc;n ngữ tr&ecirc;n c&ugrave;ng một d&ograve;ng dữ liệu.</li>
</ul>

<p style="box-sizing: border-box; margin: 0px; padding: 0px; line-height: 25px; color: rgb(68, 68, 68); font-size: 14px; text-align: justify;"><img alt="" src="/Upload/ImageDes/images/ERP01(1).png" style="width: 848px; height: 678px;" /></p>

<p style="box-sizing: border-box; margin: 0px; padding: 0px; line-height: 25px; color: rgb(68, 68, 68); font-size: 14px; text-align: center;"><strong style="box-sizing: border-box; padding: 0px; line-height: 25px;">(M&ocirc; h&igrave;nh quản l&yacute; v&agrave; li&ecirc;n kết dữ liệu giữa c&aacute;c ph&ograve;ng ban)</strong></p>

<p style="box-sizing: border-box; margin: 0px; padding: 0px; line-height: 25px; color: rgb(68, 68, 68); font-size: 14px; text-align: justify;"><img alt="" src="/Upload/ImageDes/images/ERP02(1).png" style="width: 848px; height: 555px;" /></p>

<p style="box-sizing: border-box; margin: 0px; padding: 0px; line-height: 25px; color: rgb(68, 68, 68); font-size: 14px; text-align: center;"><strong style="box-sizing: border-box; padding: 0px; line-height: 25px;">(M&ocirc; h&igrave;nh chạy online cho nhiều điểm, nhiều chi nh&aacute;nh)</strong></p>

<p style="box-sizing: border-box; margin: 0px; padding: 0px; line-height: 25px; color: rgb(68, 68, 68); font-size: 14px; text-align: justify;"><img alt="" src="/Upload/ImageDes/images/ERP03(1).png" style="width: 848px; height: 654px;" /></p>

<p style="box-sizing: border-box; margin: 0px; padding: 0px; line-height: 25px; color: rgb(68, 68, 68); font-size: 14px; text-align: center;"><strong style="box-sizing: border-box; padding: 0px; line-height: 25px;">(M&ocirc; h&igrave;nh kiểm tra v&agrave; xem b&aacute;o c&aacute;o khi đi ra b&ecirc;n ngo&agrave;i)</strong></p>
', N'<p style="box-sizing: border-box; margin: 0px; padding: 0px; line-height: 25px; color: rgb(68, 68, 68); font-family: MyriadPro; font-size: 14px; text-align: justify;"><strong style="color: rgb(68, 68, 68); font-family: MyriadPro; font-size: 14px; text-align: center; box-sizing: border-box; padding: 0px; line-height: 25px;"><img alt="" src="/Upload/ImageDes/images/ERP04%20So%20do%20tong%20quan(1).png" style="width: 651px; height: 705px;" /></strong></p>

<p style="box-sizing: border-box; margin: 0px; padding: 0px; line-height: 25px; color: rgb(68, 68, 68); font-family: MyriadPro; font-size: 14px; text-align: justify;"><span style="box-sizing: border-box; padding: 0px; line-height: 25px; color: rgb(0, 0, 128);"><strong style="box-sizing: border-box; padding: 0px; line-height: 25px;">1. HỆ THỐNG</strong></span></p>

<p class="txttext" style="box-sizing: border-box; margin: 0px; padding: 0px; line-height: 25px; color: rgb(68, 68, 68); font-family: MyriadPro; font-size: 14px; text-align: justify;">Ph&acirc;n hệ thống quản l&yacute;, li&ecirc;n kết v&agrave; gi&aacute;m s&aacute;t mọi hoạt động của c&aacute;c phần h&agrave;nh trong AcpiNet. N&oacute; đảm bảo việc đăng nhập của người d&ugrave;ng, hoạt động của mỗi chức năng đều được kiểm so&aacute;t v&agrave; lưu lại.</p>

<ul style="box-sizing: border-box; padding-right: 0px; padding-left: 15px; line-height: 25px; margin: 0px; color: rgb(68, 68, 68); font-family: MyriadPro; font-size: 14px; text-align: justify;">
	<li class="txttext" style="box-sizing: border-box; padding: 0px; line-height: 25px;"><strong style="box-sizing: border-box; padding: 0px; line-height: 25px;">Chức năng ch&iacute;nh</strong>

	<ul style="box-sizing: border-box; padding-right: 0px; padding-left: 15px; line-height: 25px; margin: 0px;">
		<li class="txttext" style="box-sizing: border-box; padding: 0px; line-height: 25px; text-align: justify;">Quản l&yacute; v&agrave; ph&acirc;n quyền người sử dụng, c&oacute; thể ph&acirc;n quyền truy cập c&aacute;c chức năng, quyền t&aacute;c động l&ecirc;n danh mục v&agrave; chứng từ (quyền nhập/sửa/xo&aacute;)</li>
	</ul>

	<ul style="box-sizing: border-box; padding-right: 0px; padding-left: 15px; line-height: 25px; margin: 0px;">
		<li class="txttext" style="box-sizing: border-box; padding: 0px; line-height: 25px;">Khai b&aacute;o th&ecirc;m, bớt c&aacute;c đơn vị cơ sở hạch to&aacute;n độc lập, phụ thuộc hoặc tổng hợp to&agrave;n ng&agrave;nh theo m&ocirc; h&igrave;nh Tổng C&ocirc;ng ty.</li>
	</ul>

	<ul style="box-sizing: border-box; padding-right: 0px; padding-left: 15px; line-height: 25px; margin: 0px;">
		<li class="txttext" style="box-sizing: border-box; padding: 0px; line-height: 25px;">Khai b&aacute;o th&ecirc;m, bớt c&aacute;c bộ phận hạch to&aacute;n trong đơn vị cơ sở(C&ocirc;ng ty, chi nh&aacute;nh)</li>
	</ul>

	<ul style="box-sizing: border-box; padding-right: 0px; padding-left: 15px; line-height: 25px; margin: 0px;">
		<li class="txttext" style="box-sizing: border-box; padding: 0px; line-height: 25px;">Khai b&aacute;o c&aacute;c tham số tuỳ chọn để ph&ugrave; hợp với đặc th&ugrave; của từng doanh nghiệp.</li>
	</ul>

	<ul style="box-sizing: border-box; padding-right: 0px; padding-left: 15px; line-height: 25px; margin: 0px;">
		<li class="txttext" style="box-sizing: border-box; padding: 0px; line-height: 25px;">Kiểm tra t&iacute;nh đồng bộ v&agrave; t&iacute;nh đ&uacute;ng đắn của số liệu.</li>
	</ul>

	<ul style="box-sizing: border-box; padding-right: 0px; padding-left: 15px; line-height: 25px; margin: 0px;">
		<li class="txttext" style="box-sizing: border-box; padding: 0px; line-height: 25px;">Lưu trữ số liệu định kỳ.</li>
	</ul>

	<ul style="box-sizing: border-box; padding-right: 0px; padding-left: 15px; line-height: 25px; margin: 0px;">
		<li class="txttext" style="box-sizing: border-box; padding: 0px; line-height: 25px;">Kho&aacute; hoặc mở dữ liệu th&aacute;ng (kh&ocirc;ng cho ph&eacute;p sửa đổi / cho ph&eacute;p sửa đổi số liệu).</li>
	</ul>

	<ul style="box-sizing: border-box; padding-right: 0px; padding-left: 15px; line-height: 25px; margin: 0px;">
		<li class="txttext" style="box-sizing: border-box; padding: 0px; line-height: 25px;">Chọn năm l&agrave;m việc (l&agrave;m việc với số liệu của năm n&agrave;o)</li>
	</ul>

	<ul style="box-sizing: border-box; padding-right: 0px; padding-left: 15px; line-height: 25px; margin: 0px;">
		<li class="txttext" style="box-sizing: border-box; padding: 0px; line-height: 25px;">Chọn đơn vị cơ sở l&agrave;m việc (l&agrave;m việc với số liệu của đơn vị, bộ phận n&agrave;o)</li>
	</ul>

	<ul style="box-sizing: border-box; padding-right: 0px; padding-left: 15px; line-height: 25px; margin: 0px;">
		<li class="txttext" style="box-sizing: border-box; padding: 0px; line-height: 25px; text-align: justify;">Hướng dẫn sử dụng chương tr&igrave;nh.</li>
	</ul>
	</li>
</ul>

<p style="box-sizing: border-box; margin: 0px; padding: 0px; line-height: 25px; color: rgb(68, 68, 68); font-family: MyriadPro; font-size: 14px; text-align: justify;"><span style="box-sizing: border-box; padding: 0px; line-height: 25px; color: rgb(0, 0, 128);"><strong style="box-sizing: border-box; padding: 0px; line-height: 25px;">2. KẾ TO&Aacute;N TỔNG HỢP</strong></span></p>

<div class="sanpham2" style="box-sizing: border-box; padding: 0px; line-height: 25px; color: rgb(68, 68, 68); font-family: MyriadPro; font-size: 14px; text-align: justify;">
<p class="txttext" style="box-sizing: border-box; margin: 0px; padding: 0px; line-height: 25px;">Ph&acirc;n hệ kế to&aacute;n tổng hợp l&agrave; nền tảng của hệ thống kế to&aacute;n, được thiết kế mềm dẻo để đ&aacute;p ứng đ&aacute;p ứng y&ecirc;u cầu của c&aacute;c c&ocirc;ng ty c&oacute; quy m&ocirc; v&agrave; m&ocirc; h&igrave;nh kh&aacute;c nhau. N&oacute; cung cấp nhiều chức năng phục vụ cho việc hoạch định kế hoạch v&agrave; c&aacute;c quy tr&igrave;nh xử l&yacute; cần thiết. Ph&acirc;n hệ kế to&aacute;n tổng hợp li&ecirc;n kết với tất cả c&aacute;c ph&acirc;n hệ của hệ thống, nơi cung cấp đầy đủ v&agrave; ch&iacute;nh x&aacute;c c&aacute;c dữ liệu t&agrave;i ch&iacute;nh.</p>
</div>

<ul style="box-sizing: border-box; padding-right: 0px; padding-left: 15px; line-height: 25px; margin: 0px; color: rgb(68, 68, 68); font-family: MyriadPro; font-size: 14px; text-align: justify;">
	<li class="sanpham2" style="box-sizing: border-box; padding: 0px; line-height: 25px;">&nbsp;<strong style="box-sizing: border-box; padding: 0px; line-height: 25px;">C&aacute;c chức năng ch&iacute;nh</strong>

	<ul style="box-sizing: border-box; padding-right: 0px; padding-left: 15px; line-height: 25px; margin: 0px;">
		<li style="box-sizing: border-box; padding: 0px; line-height: 25px; text-align: justify;">Cập nhật c&aacute;c chứng từ kế to&aacute;n tổng qu&aacute;t, ph&acirc;n bổ, kết chuyển tự động.</li>
		<li style="box-sizing: border-box; padding: 0px; line-height: 25px; text-align: justify;">Tạo v&agrave; quản l&yacute; danh mục t&agrave;i khoản, độ d&agrave;i số hiệu c&aacute;c t&agrave;i khoản cho ph&eacute;p 20 k&yacute; tự.</li>
		<li style="box-sizing: border-box; padding: 0px; line-height: 25px; text-align: justify;">Trang th&aacute;i của t&agrave;i khoản Sử dụng/Kh&ocirc;ng sử dụng, cho ph&eacute;p quản l&yacute; v&agrave; in lại số liệu của c&aacute;c t&agrave;i khoản kh&ocirc;ng c&ograve;n sử dụng.</li>
		<li style="box-sizing: border-box; padding: 0px; line-height: 25px; text-align: justify;">Quản l&yacute; v&agrave; ph&acirc;n chia c&aacute;c kỳ kế to&aacute;n, thực hiện c&aacute;c c&ocirc;ng việc kho&aacute; sổ cuối kỳ.</li>
		<li style="box-sizing: border-box; padding: 0px; line-height: 25px; text-align: justify;">Khai b&aacute;o quy tr&igrave;nh v&agrave; thực hiện việc chuyển số liệu từ c&aacute;c ph&acirc;n hệ v&agrave;o số c&aacute;i.</li>
		<li style="box-sizing: border-box; padding: 0px; line-height: 25px; text-align: justify;">Từ sổ nhật k&yacute; v&agrave; số c&aacute;i c&oacute; thể truy ngược lại c&aacute;c chứng từ gốc</li>
		<li style="box-sizing: border-box; padding: 0px; line-height: 25px; text-align: justify;">Thực hiện kho&aacute; số liệu, kh&ocirc;ng cho ph&eacute;p người d&ugrave;ng thay đổi khi số liệu đ&atilde; được chuyển l&ecirc;n sổ c&aacute;i</li>
		<li style="box-sizing: border-box; padding: 0px; line-height: 25px; text-align: justify;">Khai b&aacute;o định mức/kế hoạch ng&acirc;n s&aacute;ch cho từng t&agrave;i khoản v&agrave; c&aacute;c đối tượng. Tự động tạo số kế hoạch theo số thực tế của kỳ kế to&aacute;n do người d&ugrave;ng lựa chọn.</li>
		<li style="box-sizing: border-box; padding: 0px; line-height: 25px; text-align: justify;">Cho ph&eacute;p chuyển/huỷ chuyển c&aacute;c b&uacute;t to&aacute;n từ c&aacute;c ph&acirc;n hệ kh&aacute;c v&agrave;o sổ c&aacute;i.</li>
	</ul>
	</li>
</ul>

<div style="box-sizing: border-box; padding: 0px; line-height: 25px; color: rgb(68, 68, 68); font-family: MyriadPro; font-size: 14px; text-align: justify;">
<div class="sanpham1" style="box-sizing: border-box; padding: 0px; line-height: 25px;"><span style="box-sizing: border-box; padding: 0px; line-height: 25px; color: rgb(0, 0, 128);"><strong style="box-sizing: border-box; padding: 0px; line-height: 25px;">3. TIỀN MẶT - NG&Acirc;N H&Agrave;NG</strong></span></div>

<div class="sanpham1" style="box-sizing: border-box; padding: 0px; line-height: 25px;">Ph&acirc;n hệ kế to&aacute;n Tiền mặt, tiền gửi ng&acirc;n h&agrave;ng d&ugrave;ng thực hiện v&agrave; quản l&yacute; c&aacute;c hoạt động thu, chi, thanh to&aacute;n, chuyển khoản,... li&ecirc;n quan đến tiền mặt, tiền gửi ng&acirc;n h&agrave;ng hoặc tiền vay bằng đồng nội tệ hoặc ngoại tệ để đưa ra c&aacute;c b&aacute;o c&aacute;o về quỹ tiền mặt, tiền gửi v&agrave; tiền vay.</div>

<div class="sanpham1" style="box-sizing: border-box; padding: 0px; line-height: 25px;">
<p style="box-sizing: border-box; margin: 0px; padding: 0px; line-height: 25px;"><strong style="color: rgb(68, 68, 68); font-family: MyriadPro; font-size: 14px; text-align: center; box-sizing: border-box; padding: 0px; line-height: 25px;"><img alt="" src="/Upload/ImageDes/images/ERP05%20Phan%20he%20tien%20mat(1).png" style="width: 848px; height: 286px;" /></strong></p>

<ul style="box-sizing: border-box; padding-right: 0px; padding-left: 15px; line-height: 25px; margin: 0px;">
	<li style="box-sizing: border-box; padding: 0px; line-height: 25px; text-align: justify;"><strong style="box-sizing: border-box; padding: 0px; line-height: 25px;">Chức năng ch&iacute;nh</strong>

	<ul style="box-sizing: border-box; padding-right: 0px; padding-left: 15px; line-height: 25px; margin: 0px;">
		<li class="txttext" style="box-sizing: border-box; padding: 0px; line-height: 25px;">Ph&acirc;n hệ n&agrave;y d&ugrave;ng để cập nhật chứng từ thu, chi theo kh&aacute;ch h&agrave;ng, hợp đồng h&oacute;a đơn mua b&aacute;n v&agrave; theo c&aacute;c khoản mục chi ph&iacute; từ quỹ tiền mặt hoặc tiền gửi ng&acirc;n h&agrave;ng.</li>
		<li class="txttext" style="box-sizing: border-box; padding: 0px; line-height: 25px;">Cho ph&eacute;p in từ chương tr&igrave;nh c&aacute;c mẫu Phiếu thu, Phiếu chi, Ủy nhiệm chi theo mẫu đặc th&ugrave; v&agrave; người d&ugrave;ng c&oacute; thể chỉnh sửa lại. Hệ thống c&oacute; sẵn nhiều mẫu Ủy nhiệm chi theo quy định của c&aacute;c ng&acirc;n h&agrave;ng.</li>
		<li class="txttext" style="box-sizing: border-box; padding: 0px; line-height: 25px;">Hệ thống cho ph&eacute;p thu, chi tiền cho nhiều đối tượng kh&aacute;ch h&agrave;ng, nh&agrave; cung cấp tr&ecirc;n c&ugrave;ng một chứng từ thu hoặc chi.</li>
		<li class="txttext" style="box-sizing: border-box; padding: 0px; line-height: 25px;">Chương tr&igrave;nh cho ph&eacute;p theo d&otilde;i tạm ứng v&agrave; thực hiện thanh to&aacute;n theo từng khoản tạm ứng.</li>
		<li class="txttext" style="box-sizing: border-box; padding: 0px; line-height: 25px;">Cập nhật, theo d&otilde;i v&agrave; quản l&yacute; c&aacute;c khế ước vay tiền ng&acirc;n h&agrave;ng, khoản vay của c&aacute;c c&aacute; nh&acirc;n, đơn vị. Hệ thống t&iacute;nh l&atilde;i theo từng khoản khế ước v&agrave; khoản vay.</li>
	</ul>
	</li>
</ul>

<p style="box-sizing: border-box; margin: 0px; padding: 0px; line-height: 25px;"><span style="box-sizing: border-box; padding: 0px; line-height: 25px; color: rgb(0, 0, 128);">&nbsp;<strong style="box-sizing: border-box; padding: 0px; line-height: 25px;">4. B&Aacute;N H&Agrave;NG</strong></span></p>

<div class="sanpham2" style="box-sizing: border-box; padding: 0px; line-height: 25px;">
<p style="box-sizing: border-box; margin: 0px; padding: 0px; line-height: 25px;">Ph&acirc;n hệ quản l&yacute; b&aacute;n h&agrave;ng quản l&yacute; to&agrave;n bộ hoạt động của quy tr&igrave;nh b&aacute;n h&agrave;ng, từ kh&acirc;u tiếp nhận đơn h&agrave;ng, xử l&yacute;, chấp nhận, thu tiền, chuyển h&agrave;ng đến khi xuất ho&aacute; đơn. Người d&ugrave;ng c&oacute; thể định nghĩa v&agrave; quyết định lại c&aacute;c bước quy tr&igrave;nh cho ph&ugrave; với doanh nghiệp của m&igrave;nh.</p>

<p style="box-sizing: border-box; margin: 0px; padding: 0px; line-height: 25px;"><img alt="" src="/Upload/ImageDes/images/ERP06%20Phan%20he%20ban%20hang.png" style="color: rgb(68, 68, 68); font-family: MyriadPro; font-size: 14px; text-align: center; width: 848px; height: 450px;" /></p>
</div>

<div class="sanpham2" style="box-sizing: border-box; padding: 0px; line-height: 25px;">
<ul style="box-sizing: border-box; padding-right: 0px; padding-left: 15px; line-height: 25px; margin: 0px;">
	<li class="txttext" style="box-sizing: border-box; padding: 0px; line-height: 25px;"><strong style="box-sizing: border-box; padding: 0px; line-height: 25px;">C&aacute;c chức năng ch&iacute;nh</strong>

	<div class="sanpham3" style="box-sizing: border-box; padding: 0px; line-height: 25px;">
	<ul style="box-sizing: border-box; padding-right: 0px; padding-left: 15px; line-height: 25px; margin: 0px;">
		<li class="txttext" style="box-sizing: border-box; padding: 0px; line-height: 25px;">Cập nhật Đơn h&agrave;ng, Phiếu giao h&agrave;ng, Ho&aacute; đơn, H&agrave;ng b&aacute;n bị trả lại</li>
	</ul>
	</div>

	<div class="sanpham3" style="box-sizing: border-box; padding: 0px; line-height: 25px;">
	<ul style="box-sizing: border-box; padding-right: 0px; padding-left: 15px; line-height: 25px; margin: 0px;">
		<li class="txttext" style="box-sizing: border-box; padding: 0px; line-height: 25px;">Quản l&yacute; c&aacute;c kh&aacute;ch h&agrave;ng, c&aacute;c địa chỉ giao h&agrave;ng của kh&aacute;ch h&agrave;ng</li>
	</ul>
